tinymce.PluginManager.add('publication', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('publication', {
        text: 'Ajouter un lien vers une publication',
        icon: false,
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Entrer l\'ID de la publication',
                body: [
                    {type: 'textbox', name: 'id', label: 'ID'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent('[PUBLICATION:' + e.data.id + ']');
                }
            });
        }
    });
});